    private void getAds() {
        ads.clear();
        referenceAdvertisements = FirebaseDatabase.getInstance().getReference().child("Ads");
        referenceAdvertisements.addValueEventListener(new ValueEventListener() {
            @Override
            public void onDataChange(@NonNull DataSnapshot dataSnapshot) {
                for(DataSnapshot adSnapshot : dataSnapshot.getChildren() ){
                    AdvertisementCarModel ad = adSnapshot.getValue(AdvertisementCarModel.class);
                    ads.add(ad);
                }
                initRecyclerAds();
            }

            @Override
            public void onCancelled(@NonNull DatabaseError databaseError) {
                Toast.makeText(HomeFragment.this.getActivity(),"Error",Toast.LENGTH_SHORT).show();
            }
        });
    }

    private void initRecyclerAds() {
        Log.d(TAG, "initRecyclerView: init recyclerview.");
        LinearLayoutManager layoutManager = new LinearLayoutManager(getActivity(), LinearLayoutManager.VERTICAL, false);
        recyclerAds.setLayoutManager(layoutManager);
        RecyclerAdsAdapter adapter = new RecyclerAdsAdapter(getActivity(), ads);
        recyclerAds.setAdapter(adapter);
        adapter.notifyDataSetChanged();
    }

    private void initRecyclerView() {
        Log.d(TAG, "initRecyclerView: init recyclerview.");
        LinearLayoutManager layoutManager = new LinearLayoutManager(getActivity(), LinearLayoutManager.HORIZONTAL, false);
        recyclerView.setLayoutManager(layoutManager);
        RecyclerViewAdapter adapter = new RecyclerViewAdapter(getActivity(), mNames, mImageUrls);
        recyclerView.setAdapter(adapter);
        adapter.notifyDataSetChanged();
    }


    public boolean isServiceOK() {
        Log.d(TAG, "isServiceOK: checking google services version");

        int available = GoogleApiAvailability.getInstance().isGooglePlayServicesAvailable(HomeFragment.this.getActivity());
        if (available == ConnectionResult.SUCCESS) {
            //play services is working
            Log.d(TAG, "isServiceOK: Google Play Services is working");
            return true;
        } else if (GoogleApiAvailability.getInstance().isUserResolvableError(available)) {
            //an error can be resolved
            Log.d(TAG, "can fix");
            Dialog dialog = GoogleApiAvailability.getInstance().getErrorDialog(HomeFragment.this.getActivity(), available, ERROR_DIALOG_REQUEST);
            dialog.show();
        } else {
            Toast.makeText(this.getActivity(), "You can't select location", Toast.LENGTH_SHORT).show();

        }
        return false;
    }

